import React from 'react';

const Carrito = () => {
    return (

    <>
        <div className="row m-5">
            <div className="col">
                <div classNameName="container text-center mt-5">
                    <h2> 
                        <p className="text-center">Tu Carrito de Compras </p>
                    </h2>

                    <div className="col-md-6">
                        <h3>Carrito de Compras</h3>
                        {carrito.length === 0 ? (
                            <p>El carrito está vacío.</p>
                        ) : (
                            <ul className="list-group">
                            {carrito.map((item, index) => (
                                <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                                {item.nombre} - ${item.valor} x {item.cantidad}
                                <button className="btn btn-danger btn-sm" onClick={() => eliminarDelCarrito(item.id)}>
                                    Eliminar
                                </button>
                                </li>
                            ))}
                            </ul>
                        )}
                        <h4 className="mt-3">Total: ${calcularTotal()}</h4>
                        <button className="btn btn-success mt-2" onClick={procesarCompra} disabled={carrito.length === 0}>
                            Comprar Ahora
                        </button>
                    </div>
                </div>
            </div>
        </div>
    </>
    );
};

export default Carrito;
