import React, { useState } from 'react';
import useProductos from '../hooks/useProductos';

const Productos = ({ addToCart }) => {
    const {
        productos,
        producto,
        setProducto,
        guardar,
        eliminar,
        cargarProductoParaEditar,
        productoIdParaEditar,
    } = useProductos();

    // Estado local para manejar las cantidades seleccionadas para cada producto
    const [cantidades, setCantidades] = useState({});

    // Función para manejar el cambio de cantidad
    const actualizarCantidad = (id, cambio) => {
        setCantidades((prev) => ({
            ...prev,
            [id]: Math.max((prev[id] || 1) + cambio, 1), // Asegura que la cantidad no sea menor a 1
        }));
    };

    return (
        <div className="container mt-5">
            <h1>Productos</h1>
            <table className="table table-striped">
                <thead className="thead-dark">
                    <tr>
                        <th>ID</th>
                        <th>Nombre</th>
                        <th>Código</th>
                        <th>Inventario</th>
                        <th>Marca</th>
                        <th>Valor</th>
                        <th>Cantidad</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {productos.map((prod) => (
                        <tr key={prod.id}>
                            <td>{prod.id}</td>
                            <td>{prod.nombre}</td>
                            <td>{prod.codigo}</td>
                            <td>{prod.inventario}</td>
                            <td>{prod.marca}</td>
                            <td>{prod.valor}</td>
                            <td>
                                {/* Sección para seleccionar cantidad */}
                                <div className="d-flex align-items-center">
                                    <button
                                        className="btn btn-outline-secondary btn-sm me-2"
                                        onClick={() => actualizarCantidad(prod.id, -1)}
                                    >
                                        -
                                    </button>
                                    <span>{cantidades[prod.id] || 1}</span>
                                    <button
                                        className="btn btn-outline-secondary btn-sm ms-2"
                                        onClick={() => actualizarCantidad(prod.id, 1)}
                                    >
                                        +
                                    </button>
                                </div>
                            </td>
                            <td>
                                <button
                                    className="btn btn-primary btn-sm me-2"
                                    onClick={() => cargarProductoParaEditar(prod)}
                                >
                                    Editar
                                </button>
                                <button
                                    className="btn btn-danger btn-sm me-2"
                                    onClick={() => eliminar(prod.id)}
                                >
                                    Eliminar
                                </button>
                                <button
                                    className="btn btn-success btn-sm"
                                    onClick={() => {
                                        const cantidadSeleccionada = cantidades[prod.id] || 1;
                                        console.log('Añadiendo producto al carrito:', prod, 'Cantidad:', cantidadSeleccionada); // Verificación
                                        addToCart(prod, cantidadSeleccionada); // Envía la cantidad seleccionada al carrito
                                    }}
                                >
                                    Añadir al carrito
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Formulario */}
            <div className="card p-4">
                <h5 className="card-title">
                    {productoIdParaEditar ? 'Actualizar Producto' : 'Añadir Producto'}
                </h5>

                <input
                    type="text"
                    className="form-control mb-3"
                    placeholder="Nombre"
                    value={producto.nombre}
                    onChange={(e) => setProducto({ ...producto, nombre: e.target.value })}
                />

                <input
                    type="text"
                    className="form-control mb-3"
                    placeholder="Codigo"
                    value={producto.codigo}
                    onChange={(e) => setProducto({ ...producto, codigo: e.target.value })}
                />

                <input
                    type="text"
                    className="form-control mb-3"
                    placeholder="Inventario"
                    value={producto.inventario}
                    onChange={(e) => setProducto({ ...producto, inventario: e.target.value })}
                />

                <input
                    type="text"
                    className="form-control mb-3"
                    placeholder="Marca"
                    value={producto.marca}
                    onChange={(e) => setProducto({ ...producto, marca: e.target.value })}
                />

                <input
                    type="text"
                    className="form-control mb-3"
                    placeholder="Valor"
                    value={producto.valor}
                    onChange={(e) => setProducto({ ...producto, valor: e.target.value })}
                />

                <button className="btn btn-success" onClick={guardar}>
                    {productoIdParaEditar ? 'Actualizar Producto' : 'Añadir Producto'}
                </button>
            </div>
        </div>
    );
};

export default Productos;
