import React, { useState } from 'react';
import Carrito from './Carrito';

const App = () => {
  const [cartItems, setCartItems] = useState([
    // Aquí van los items de ejemplo
    { id: 1, nombre: 'Producto 1', codigo: 'P001', marca: 'Marca A', valor: 100, foto: '', cantidad: 2 },
    { id: 2, nombre: 'Producto 2', codigo: 'P002', marca: 'Marca B', valor: 150, foto: '', cantidad: 1 },
    // Otros productos...
  ]);

  // Función para eliminar un item del carrito
  const removeFromCart = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id)); // Elimina el producto con el ID seleccionado
  };

  // Función para vaciar el carrito
  const clearCart = () => {
    setCartItems([]); // Vacía todo el carrito
  };

  return (
    <Carrito
      cartItems={cartItems}
      removeFromCart={removeFromCart}
      clearCart={clearCart}
    />
  );
};

export default App;
