import { useState } from 'react';
import useProductos from './useProductos';


const useCompra = () => {
  const { productos } = useProductos();
  const [carrito, setCarrito] = useState([]);

  // Añadir producto al carrito
  const agregarAlCarrito = (producto) => {
    setCarrito([...carrito, { ...producto, cantidad: 1 }]); // Inicializa cantidad a 1
  };

  // Eliminar producto del carrito
  const eliminarDelCarrito = (id) => {
    setCarrito(carrito.filter(item => item.id !== id));
  };

  // Calcular total
  const calcularTotal = () => {
    return carrito.reduce((total, item) => total + item.valor * item.cantidad, 0);
  };

  // Procesar la compra
  const procesarCompra = () => {
    // Lógica de compra (por ejemplo, enviar a una API o mostrar una confirmación)
    alert('Compra realizada con éxito');
    setCarrito([]); // Vacía el carrito
  };

  return {
    agregarAlCarrito,
    eliminarDelCarrito,
    calcularTotal,
    procesarCompra,
  }

};

export default useCompra;
