const Cart = ({ cartItems, removeFromCart, clearCart }) => {
  // Ensure each product has a "quantity" property with an initial value of 1
  const totalProducts = cartItems.reduce((acc, prod) => acc + (prod.quantity || 1), 0);

  return (
    <div className="container mt-5">
      <h1>Shopping Cart</h1>

      {/* Display the total number of products in the cart */}
      <h3>Total number of products: {totalProducts}</h3>

      {cartItems.length === 0 ? (
        <p>There are no products in the cart.</p>
      ) : (
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Code</th>
              <th>Brand</th>
              <th>Value</th>
              <th>Photo</th> {/* Column for the photo */}
              <th>Quantity</th> {/* Column for the quantity */}
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((prod) => (
              <tr key={prod.id}>
                <td>{prod.id}</td>
                <td>{prod.name}</td>
                <td>{prod.code}</td>
                <td>{prod.brand}</td>
                <td>{prod.value}</td>
                <td>
                  {/* Display the product's photo */}
                  <img src={prod.photo} alt={prod.name} style={{ width: '50px', height: '50px' }} />
                </td>
                <td>{prod.quantity}</td> {/* Display the number of products in the cart */}
                <td>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => removeFromCart(prod.id)}
                  >
                    Remove
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <button className="btn btn-danger" onClick={clearCart}>
        Empty cart
      </button>
    </div>
  );
};

export default Cart;
