import React from 'react';
import { useLocation } from 'react-router-dom';

const DetalleProducto = () => {
  const location = useLocation(); // Obtener el estado pasado
  const producto = location.state?.producto; // Acceder al producto

  if (!producto) {
    return <div>Producto no encontrado</div>;
  }

  return (
    <div className="container my-5">
      <h2>Detalle del Producto</h2>
      <div className="card mb-4">
        <img 
          src="https://via.placeholder.com/300" 
          className="card-img-top" 
          alt={producto.nombre} 
        />
        <div className="card-body">
          <h5 className="card-title">{producto.nombre}</h5>
          <p className="card-text">Código: {producto.codigo}</p>
          <p className="card-text">Marca: {producto.marca}</p>
          <p className="card-text">Inventario: {producto.inventario}</p>
          <p className="card-text">Precio: ${producto.valor}</p>
          <button className="btn btn-primary">Añadir al carrito</button>
        </div>
      </div>
    </div>
  );
};

export default DetalleProducto;
