import React, { useState } from 'react';
import useProductos from '../hooks/useProductos';

const Compra = () => {
  const { productos } = useProductos();
  const [carrito, setCarrito] = useState([]);

  // Añadir producto al carrito
  const agregarAlCarrito = (producto) => {
    setCarrito([...carrito, { ...producto, cantidad: 1 }]); // Inicializa cantidad a 1
  };

  // Eliminar producto del carrito
  const eliminarDelCarrito = (id) => {
    setCarrito(carrito.filter(item => item.id !== id));
  };

  // Calcular total
  const calcularTotal = () => {
    return carrito.reduce((total, item) => total + item.valor * item.cantidad, 0);
  };

  // Procesar la compra
  const procesarCompra = () => {
    // Lógica de compra (por ejemplo, enviar a una API o mostrar una confirmación)
    alert('Compra realizada con éxito');
    setCarrito([]); // Vacía el carrito
  };

  return (
    <div className="container mt-5">
      <h1>Compra de Productos</h1>
      <div className="row">
        <div className="col-md-6">
          <h3>Lista de Productos</h3>
          <ul className="list-group">
            {productos.map((prod) => (
              <li key={prod.id} className="list-group-item d-flex justify-content-between align-items-center">
                {prod.nombre} - ${prod.valor}
                <button className="btn btn-primary btn-sm" onClick={() => agregarAlCarrito(prod)}>
                  Añadir al Carrito
                </button>
              </li>
            ))}
          </ul>
        </div>

        <div className="col-md-6">
          <h3>Carrito de Compras</h3>
          {carrito.length === 0 ? (
            <p>El carrito está vacío.</p>
          ) : (
            <ul className="list-group">
              {carrito.map((item, index) => (
                <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                  {item.nombre} - ${item.valor} x {item.cantidad}
                  <button className="btn btn-danger btn-sm" onClick={() => eliminarDelCarrito(item.id)}>
                    Eliminar
                  </button>
                </li>
              ))}
            </ul>
          )}
          <h4 className="mt-3">Total: ${calcularTotal()}</h4>
          <button className="btn btn-success mt-2" onClick={procesarCompra} disabled={carrito.length === 0}>
            Comprar Ahora
          </button>
        </div>
      </div>
    </div>
  );
};

export default Compra;
