import React from 'react';

const Carrito = ({ cartItems, removeFromCart, clearCart }) => {
  // Calcular cantidad total de productos
  const totalProductos = cartItems.reduce((acc, prod) => acc + prod.cantidad, 0);

  // Calcular el subtotal, impuesto (IVA) y total
  const subtotal = cartItems.reduce((acc, prod) => acc + (prod.valor * prod.cantidad), 0);
  const impuesto = subtotal * 0.19; // Suponiendo que el impuesto (IVA) es del 19%
  const total = subtotal + impuesto;

  // Formatear los valores a pesos colombianos (COP)
  const formatCurrency = (value) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
    }).format(value);
  };

  return (
    <div className="container mt-5">
      <h1>Carrito de Compras</h1>

      {/* Sección para mostrar la cantidad total de productos y el botón para vaciar el carrito */}
      <div className="d-flex justify-content-between mb-4">
        <h3>Cantidad total de productos: {totalProductos}</h3>
        <button className="btn btn-danger" onClick={clearCart}>
          Vaciar carrito
        </button>
      </div>

      {cartItems.length === 0 ? (
        <p>No hay productos en el carrito.</p>
      ) : (
        <>
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Nombre</th>
                <th>Código</th>
                <th>Marca</th>
                <th>Valor</th>
                <th>Foto</th>
                <th>Cantidad</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((prod) => (
                <tr key={prod.id}>
                  <td>{prod.id}</td>
                  <td>{prod.nombre}</td>
                  <td>{prod.codigo}</td>
                  <td>{prod.marca}</td>
                  <td>{formatCurrency(prod.valor)}</td>
                  <td>
                    <img
                      src={prod.foto || 'https://via.placeholder.com/50'}
                      alt={prod.nombre}
                      style={{ width: '50px', height: '50px' }}
                    />
                  </td>
                  <td>{prod.cantidad}</td>
                  <td>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => removeFromCart(prod.id)} // Eliminar el producto seleccionado
                    >
                      Eliminar
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Apartado para mostrar subtotal, impuesto (IVA) y total */}
          <div className="d-flex justify-content-end mt-4">
            <div>
              <p><strong>Subtotal:</strong> {formatCurrency(subtotal)}</p>
              <p><strong>Impuesto (IVA 19%):</strong> {formatCurrency(impuesto)}</p>
              <p><strong>Total:</strong> {formatCurrency(total)}</p>
            </div>
          </div>

          {/* Botón "Ir a Pagar" en verde */}
          <div className="d-flex justify-content-end mt-2"> {/* Margin de 2px entre total y botón */}
            <button className="btn btn-success" onClick={() => alert('Ir a Pagar')}>
              Ir a Pagar
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Carrito;
