import React, { useState } from "react";
import axios from "axios";

const Productos = () => {
  const [producto, setProducto] = useState({
    nombre: "",
    codigo: "",
    inventario: 0,
    marca: "",
    valor: 0,
    estado: 1,
    imagen: null, // Nuevo estado para la imagen
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProducto({
      ...producto,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    setProducto({
      ...producto,
      imagen: e.target.files[0], // Guardar la imagen como archivo
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Crear un FormData para enviar la imagen y otros datos
    const formData = new FormData();
    for (const key in producto) {
      formData.append(key, producto[key]);
    }

    try {
      const response = await axios.post("http://localhost:5000/api/productos", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      console.log("Producto guardado:", response.data);
    } catch (error) {
      console.error("Error al guardar el producto:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Nombre:</label>
        <input
          type="text"
          name="nombre"
          value={producto.nombre}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>CÃ³digo:</label>
        <input
          type="text"
          name="codigo"
          value={producto.codigo}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Inventario:</label>
        <input
          type="number"
          name="inventario"
          value={producto.inventario}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Marca:</label>
        <input
          type="text"
          name="marca"
          value={producto.marca}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Valor:</label>
        <input
          type="number"
          name="valor"
          value={producto.valor}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Estado:</label>
        <select
          name="estado"
          value={producto.estado}
          onChange={handleChange}
        >
          <option value={1}>Activo</option>
          <option value={0}>Inactivo</option>
        </select>
      </div>
      <div>
        <label>Imagen:</label>
        <input
          type="file"
          name="imagen"
          accept="image/*"
          onChange={handleFileChange}
        />
      </div>
      <button type="submit">Guardar Producto</button>
    </form>
  );
};

export default Productos;
