import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const LoginForm = ({ setIsAuthenticated }) => {
    const [form, setForm] = useState({ email: '', password: '' });
    const [rememberMe, setRememberMe] = useState(false); // Estado para recordar usuario
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    // Cargar correo almacenado al montar el componente
    useEffect(() => {
        const storedEmail = localStorage.getItem('rememberedEmail');
        if (storedEmail) {
            setForm((prevForm) => ({ ...prevForm, email: storedEmail }));
            setRememberMe(true); // Marcar la opción si el correo está almacenado
        }
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:5000/login', form);
            localStorage.setItem('token', res.data.token); // Guarda el token
            setIsAuthenticated(true); // Actualiza el estado a autenticado

            // Guardar o eliminar correo en localStorage según el checkbox
            if (rememberMe) {
                localStorage.setItem('rememberedEmail', form.email);
            } else {
                localStorage.removeItem('rememberedEmail');
            }

            navigate('/productos'); // Redirige a productos
        } catch (err) {
            setMessage(err.response?.data?.message || 'Error al iniciar sesión');
        }
    };

    return (
        <div className="container mt-5">
            <h2>Inicio de Sesión</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <input
                        type="email"
                        className="form-control"
                        placeholder="Correo Electrónico"
                        value={form.email}
                        onChange={(e) => setForm({ ...form, email: e.target.value })}
                    />
                </div>
                <div className="mb-3">
                    <input
                        type="password"
                        className="form-control"
                        placeholder="Contraseña"
                        value={form.password}
                        onChange={(e) => setForm({ ...form, password: e.target.value })}
                    />
                </div>
                <div className="form-check mb-3">
                    <input
                        type="checkbox"
                        className="form-check-input"
                        id="rememberMe"
                        checked={rememberMe}
                        onChange={(e) => setRememberMe(e.target.checked)}
                    />
                    <label className="form-check-label" htmlFor="rememberMe">
                        Recordar Usuario
                    </label>
                </div>
                <button className="btn btn-primary" type="submit">Iniciar Sesión</button>
                {message && <p className="mt-3 text-danger">{message}</p>}
            </form>
        </div>
    );
};

export default LoginForm;
